        "name": "profile.lastName",
        "value": "Doe",
    },
])
```
```csharp
using Pulumi;
using Okta = Pulumi.Okta;

class MyStack : Stack
{
    public MyStack()
    {
        var example = Output.Create(Okta.User.GetUser.InvokeAsync(new Okta.User.GetUserArgs
        {
            Searches = 
            {
                new Okta.User.Inputs.GetUserSearchArgs
                {
                    Name = "profile.firstName",
                    Value = "John",
                },
                new Okta.User.Inputs.GetUserSearchArgs
                {
                    Name = "profile.lastName",
                    Value = "Doe",
                },
            },
        }));
    }

}
