)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := user.LookupUser(ctx, &user.LookupUserArgs{
			Searches: []user.GetUserSearch{
				user.GetUserSearch{
					Name:  "profile.firstName",
					Value: "John",
				},
