import * as pulumi from "@pulumi/pulumi";
import * as okta from "@pulumi/okta";

const example = new okta.app.SecurePasswordStore("example", {
    label: "example",
    usernameField: "user",
    passwordField: "pass",
    url: "https://test.com",
    credentialsScheme: "ADMIN_SETS_CREDENTIALS",
});
```
