    name: "My Policy",
    type: "PROFILE_ENROLLMENT",
});
const test = okta.app.getApp({
    label: "My App",
});
const examplePolicyProfileEnrollmentApps = new okta.PolicyProfileEnrollmentApps("example", {
    policyId: exampleOktaPolicy.id,
    apps: [id],
});
