import * as pulumi from "@pulumi/pulumi";
import * as okta from "@pulumi/okta";

const example = new okta.FactorTotp("example", {
    clockDriftInterval: 10,
    hmacAlgorithm: "HMacSHA256",
    otpLength: 10,
    sharedSecretEncoding: "hexadecimal",
    timeStep: 30,
});
