    deleteWhenAbsent: true,
    mappings: [
        {
            expression: "appuser.firstName",
            id: "firstName",
        },
        {
            expression: "appuser.lastName",
            id: "lastName",
        },
        {
            expression: "appuser.email",
            id: "email",
        },
        {
            expression: "appuser.email",
            id: "login",
        },
    ],
    sourceId: "<source id>",
    targetId: user.then(user => user.id),
});
